"use strict";

var $loginContainer = document.querySelector('.login_container');
var $gameboardContainer = document.querySelector('.gameboard_container');
var $loginForm = document.querySelector('.login_form');
var $nicknameInput = document.querySelector('.login_nickname');
var $passwordInput = document.querySelector('.login_password');
var $loginButton = document.querySelector('.login_button');
var $message = document.querySelector('.login_message');
var nickname = localStorage.getItem('nickname');
var password = localStorage.getItem('password');

function setNickname(nickname) {
  localStorage.setItem('nickname', nickname);
}

function setPassword(password) {
  localStorage.setItem('password', password);
}

function getInputValue() {
  var nickname = $nicknameInput.value;
  var password = $passwordInput.value;
  return {
    nickname: nickname,
    password: password
  };
}

function handleLogin(e) {
  e.preventDefault();
  var inputObj = getInputValue();

  if (nickname && password) {
    if (nickname !== inputObj.nickname || password !== inputObj.password) {
      $message.textContent = 'Check your nickname or password ðŸ˜±';
      return;
    }
  } else {
    setNickname(inputObj.nickname);
    setPassword(inputObj.password);
  }

  $loginContainer.classList.add('hidden');
  $gameboardContainer.classList.remove('hidden');
}

$loginForm.addEventListener('submit', handleLogin);
$loginButton.addEventListener('click', handleLogin);
